import Vue from 'vue'
import App from './App.vue'
import router from './router'

import'mint-ui/lib/style.css'  //mint-ui 的样式属性
import {Header,Swipe, SwipeItem ,Button} from 'mint-ui' //引入mint-UI 中header组件


Vue.config.productionTip = false
//5: 设置请求的根路径 
//Vue.http.options.root = "http://127.0.0.1/vue_ser/";
//6:全局设置post 时候表音的数据组织格式为 application/x-www-form-urlencoded
//Vue.http.options.emulateJSON = true;
// 导入 MUI 的样式表， 和 Bootstrap 用法没有差别
import './lib/mui/css/mui.css'
// 导入 MUI 的样式表，扩展图标样式，购物车图标
// 还需要加载图标字体文件
import './lib/mui/css/icons-extra.css'


Vue.component(Header.name,Header)
Vue.component(Swipe.name,Swipe)
Vue.component(SwipeItem.name,SwipeItem)
Vue.component(Button.name,Button)
//5引入axios
import axios from "axios"
//5.1配置跨域访问保存session
axios.defaults.withCredentials=true;
//5.2配置到vue 实例对象中
Vue.prototype.axios=axios;
// new 7.1:加载第三方模块  qs
import qs  from "qs";
// new 7.2:配置qs模块，qs成功Vue属性
Vue.prototype.qs = qs;
//创建日期的过滤器
Vue.filter("datetimeFilter",function(val){
    //创建日期对象，获取年月日时分秒，返回字符串：年-月-日-时-分-秒
    var date=new Date(val);
    var y=date.getFullYear();
    var m=date.getMonth()+1;
    var d=date.getDate();
    var h=date.getHours();
    var minu=date.getMinutes();
    var s=date.getSeconds();
    m<10&&(m="0"+m);
    d<10&&(d="0"+d);
    return `${y}-${m}-${d} ${h}:${minu}:${s}`

})
Vue.filter("timeFilter",function(val){
  var date=new Date(val);
  var y=date.getFullYear();
  var m=date.getMonth()+1;
  var d=date.getDate();
  m<10&&(m="0"+m);
    d<10&&(d="0"+d);
    return `${y}-${m}-${d} `
})
new Vue({
  router,
  render: h => h(App)
}).$mount('#app')
